mapper.select:
SELECT Q_BOARD_ID, Q_TITLE, CREATE_DATE, A_STATUS, MEMBER_ID, NICKNAME FROM ( SELECT ROWNUM AS RNUM, Q_BOARD_ID, Q_TITLE, CREATE_DATE, A_STATUS, MEMBER_ID, NICKNAME FROM( SELECT Q.Q_BOARD_ID, Q.Q_TITLE, Q.CREATE_DATE, Q.A_STATUS, M.MEMBER_ID, M.NICKNAME FROM TBL_QUESTION Q JOIN TBL_MEMBER M ON Q.MEMBER_ID = M.MEMBER_ID ORDER BY Q.CREATE_DATE DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1) * #{amount}
