mapper.select:
SELECT COUNT(MEMBER_ID)
FROM TBL_MEMBER
WHERE LOGIN_ID = #{loginId}

SELECT COUNT(MEMBER_ID)
FROM TBL_MEMBER
WHERE NICKNAME = #{nickname}

SELECT COUNT(EMAIL)
FROM TBL_MEMBER
WHERE EMAIL = #{email}

SELECT MEMBER_ID
FROM TBL_MEMBER
WHERE LOGIN_ID = #{loginId} AND PASSWORD = #{password}
