mapper.select:
SELECT COUNT(Q_BOARD_ID)
FROM TBL_QUESTION
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE TBL_QUESTION
SET Q_TITLE =#{qTitle}, Q_CONTENT = #{qContent}, CREATE_DATE = SYSDATE
WHERE Q_BOARD_ID = #{qBoardId}

UPDATE TBL_QUESTION
SET VIEW_COUNT = VIEW_COUNT +1
WHERE Q_BOARD_ID = #{qBoardId}

mapper.select:
SELECT Q_BOARD_ID, A_STATUS, Q_TITLE, MEMBER_ID, NICKNAME, CREATE_DATE, VIEW_COUNT FROM( SELECT ROWNUM AS RNUM, Q_BOARD_ID, A_STATUS, Q_TITLE, MEMBER_ID, NICKNAME, CREATE_DATE, VIEW_COUNT FROM( SELECT Q.Q_BOARD_ID, Q.A_STATUS, Q.Q_TITLE, M.MEMBER_ID, M.NICKNAME, TO_CHAR(Q.CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE, Q.VIEW_COUNT FROM TBL_QUESTION Q JOIN TBL_MEMBER M ON Q.MEMBER_ID = M.MEMBER_ID WHERE M.MEMBER_ID = #{memberId} ORDER BY Q.Q_BOARD_ID DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1) * #{amount}
